import { RECEIVE_USERS, TOGGLE_USERS } from "../actions/users";

export default function users(state = {}, action) {
  switch (action.type) {
    case RECEIVE_USERS:
    return {
        ...state,
        ...action.users
      };

      case TOGGLE_USERS:
      return {
        ...state,
        [action.authedUser]: {
            ...state.users[action.authedUser],
            answers:{
              ...state.users[action.authedUser].answers,
              [action.qid]: action.answer
            }
        },
      };

    default:
      return state;
  }
}
