import { RECEIVE_QUESTIONS, TOGGLE_QUESTIONS, ADD_QUESTIONS } from "../actions/questions";

export default function questions(state = {}, action) {
  switch (action.type) {
    case RECEIVE_QUESTIONS:
    return {
        ...state,
        ...action.questions,
      };

      case TOGGLE_QUESTIONS:
      return {
        ...state,
        [action.qid]: {
            ...state[action.qid].optionOne,
              votes:
                  action.answer === 'optionOne' ? state[action.qid].optionOne.votes.concat([action.authedUser]) : state[action.qid].optionOne.votes,
            ...state[action.qid].optionTwo,
              votes:
                  action.answer === 'optionTwo' ? state[action.qid].optionTwo.votes.concat([action.authedUser]) : state[action.qid].optionTwo.votes,
        },
      };

     case ADD_QUESTIONS:
        const { questions } = action;
        return {
            ...state,
            ...questions,
        };

    default:
      return state;
  }
}
